%% Direction Features
features = [];
% dividing given image(36x36) into 3x3 zones
[row, col] = size(image);
zone_height = row / 3; % 12 pixels
zone_width = col / 3; % 12 pixels

zone11 = image(1:zone_height, 1:zone_width); %imshow(zone11);pause;
zone12 = image(1:zone_height, (zone_width + 1):2 * zone_width); %imshow(zone12);pause;
zone13 = image(1:zone_height, (2 * zone_width + 1):end); %imshow(zone13);pause;

zone21 = image((zone_height + 1):2 * zone_height, 1:zone_width); %imshow(zone21);pause;
zone22 = image((zone_height + 1):2 * zone_height, (zone_width + 1):2 * zone_width); %imshow(zone22);pause;
zone23 = image((zone_height + 1):2 * zone_height, (2 * zone_width + 1):end); %imshow(zone23);pause;

zone31 = image((2 * zone_height + 1):end, 1:zone_width); %imshow(zone31);pause;
zone32 = image((2 * zone_height + 1):end, (zone_width + 1):2 * zone_width); %imshow(zone32);pause;
zone33 = image((2 * zone_height + 1):end, (2 * zone_width + 1):end); %imshow(zone33);pause;

% feature_vectors
zone11_features = lineclassifier(zone11);
zone12_features = lineclassifier(zone12);
zone13_features = lineclassifier(zone13);

zone21_features = lineclassifier(zone21);
zone22_features = lineclassifier(zone22);
zone23_features = lineclassifier(zone23);

zone31_features = lineclassifier(zone31);
zone32_features = lineclassifier(zone32);
zone33_features = lineclassifier(zone33);


